import React, { useState, useEffect } from 'react';
import { makeStyles } from "@material-ui/core/styles";
import { Typography, CircularProgress, Button, Grid } from "@material-ui/core";
import { toFirstCharUppercase } from "./constants";
import axios from "axios";


import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Avatar from '@material-ui/core/Avatar';
import { red } from '@material-ui/core/colors';
import Divider from '@material-ui/core/Divider';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme) => ({
    root: {
      paddingTop: "20px",
      maxWidth: 700,
      margin: "auto",
      backgroundColor: "#c8f3e7a3",
    },
    media: {
      margin: "auto",
    },
    avatar: {
      backgroundColor: red[500],
    },
    backButton: {
      alignItems: "right",
    },
    table: {
        minWidth: 650,
        backgroundColor: "#c8f3e7a3",
    },
}));

const Pokemon = (props) => {
  const classes = useStyles();
  const { history, match } = props;
  const { params } = match;
  const { pokemonId } = params;
  const [pokemon, setPokemon] = useState(undefined)

  useEffect(() => {
    axios 
    .get(`https://pokeapi.co/api/v2/pokemon/${pokemonId}/`)
    .then(function (response) {
      const { data } = response;
      setPokemon(data);
    })
    .catch(function (error) {
      setPokemon(false);
    });
  }, [pokemonId]);

  const generatePokemonJSX = () => {
    const { name, id, species, height, weight, sprites } = pokemon;
    const fullImageUrl = `https://pokeres.bastionbot.org/images/pokemon/${id}.png`;
    const { front_default } = sprites;

    function createData(name, calories) {
        return { name, calories};
    }

      const rows = [
        createData('Species', `${species.name}` ),
        createData('Height', `${height}`),
        createData('Weight', `${weight}`),
      ];

    return (
      <>


        <Card className={classes.root}>
        <CardHeader
            avatar={
            <Avatar aria-label="recipe" className={classes.avatar}>
                { name.charAt(0) }
            </Avatar>
            }
            title={
            <Typography component="h5" variant="h5">
              {toFirstCharUppercase(name)}
            </Typography>
            }
        />
        <CardMedia
            className={classes.media}
            image={ fullImageUrl} 
            style= {{ width: "450px", height: "450px" }}
        />
        <CardContent />
        <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="simple table">
                <TableBody>
                    {rows.map((row) => (
                        <TableRow key={row.name}>
                        <TableCell component="th" scope="row">
                            {row.name}
                        </TableCell>
                        <TableCell align="left">{row.calories}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
        </Card>
      </>
    );
  }
  return ( <> 
    {pokemon === undefined && <CircularProgress />}
    {pokemon !== undefined && pokemon && generatePokemonJSX()}
    {pokemon === false && <Typography> Pokemon not found</Typography>}
    {pokemon !== undefined && (
      <Grid container justify="flex-end">
        <Button variant = "contained" 
        onClick={() => history.push("/")}>
        back to pokedex
        </Button>
      </Grid>
    )}
  </>);

};

export default Pokemon
